/** * Set the input to use for the Job. This queries the metadata server with * the specified partition predicates, gets the matching partitions, puts * the information in the conf object. The inputInfo object is updated with * information needed in the client context * @param job the job object * @param dbName the database where the table lies * @param tableName the table to read * @param filter the partition filter to use * @throws IOException the exception in communicating with the metadata server */ public static void setInput(Job job, String dbName, String tableName, String filter) throws IOException;
/** * Set the schema for the HCatRecord data returned by HCatInputFormat. * @param job the job object * @param hcatSchema the schema to use as the consolidated schema */ public static void setOutputSchema(Job job,HCatSchema hcatSchema) throws IOException;
/** * Get the HCatTable schema for the table specified in the HCatInputFormat.setInput * call on the specified job context. This information is available only after * HCatInputFormat.setInput has been called for a JobContext. * @param context the context * @return the table schema * @throws IOException if HCatInputFormat.setInput has not been called * for the current context */ public static HCatSchema getTableSchema(JobContext context) throws IOException;

/** * Set the information about the output to write for the job. This queries the metadata * server to find the StorageHandler to use for the table. It throws an error if the * partition is already published. * @param job the job object * @param outputJobInfo the table output information for the job * @throws IOException the exception in communicating with the metadata server */ @SuppressWarnings("unchecked") public static void setOutput(Job job, OutputJobInfo outputJobInfo) throws IOException;
/** * Set the schema for the data being written out to the partition. The * table schema is used by default for the partition if this is not called. * @param job the job object * @param schema the schema for the data * @throws IOException */ public static void setSchema(final Job job, final HCatSchema schema) throws IOException;
/** * Get the table schema for the table specified in the HCatOutputFormat.setOutput call * on the specified job context. * @param context the context * @return the table schema * @throws IOException if HCatOutputFormat.setOutput has not been called * for the passed context */ public static HCatSchema getTableSchema(JobContext context) throws IOException;

public class GroupByAge extends Configured implements Tool {

    public static class Map extends
            Mapper<WritableComparable, HCatRecord, IntWritable, IntWritable> {

        int age;

        @Override
        protected void map(
                WritableComparable key,
                HCatRecord value,
                org.apache.hadoop.mapreduce.Mapper<WritableComparable, HCatRecord, 
                        IntWritable, IntWritable>.Context context)
                throws IOException, InterruptedException {
            age = (Integer) value.get(1);
            context.write(new IntWritable(age), new IntWritable(1));
        }
    }

    public static class Reduce extends Reducer<IntWritable, IntWritable,
    WritableComparable, HCatRecord> {


      @Override 
      protected void reduce(
              IntWritable key,
              java.lang.Iterable<IntWritable> values, 
              org.apache.hadoop.mapreduce.Reducer<IntWritable, IntWritable,
                      WritableComparable, HCatRecord>.Context context)
              throws IOException, InterruptedException {
          int sum = 0;
          Iterator<IntWritable> iter = values.iterator();
          while (iter.hasNext()) {
              sum++;
              iter.next();
          }
          HCatRecord record = new DefaultHCatRecord(2);
          record.set(0, key.get());
          record.set(1, sum);

          context.write(null, record);
        }
    }

    public int run(String[] args) throws Exception {
        Configuration conf = getConf();
        args = new GenericOptionsParser(conf, args).getRemainingArgs();

        String inputTableName = args[0];
        String outputTableName = args[1];
        String dbName = null;

        Job job = new Job(conf, "GroupByAge");
        HCatInputFormat.setInput(job, dbName,
                inputTableName);
        // initialize HCatOutputFormat

        job.setInputFormatClass(HCatInputFormat.class);
        job.setJarByClass(GroupByAge.class);
        job.setMapperClass(Map.class);
        job.setReducerClass(Reduce.class);
        job.setMapOutputKeyClass(IntWritable.class);
        job.setMapOutputValueClass(IntWritable.class);
        job.setOutputKeyClass(WritableComparable.class);
        job.setOutputValueClass(DefaultHCatRecord.class);
        HCatOutputFormat.setOutput(job, OutputJobInfo.create(dbName,
                outputTableName, null));
        HCatSchema s = HCatOutputFormat.getTableSchema(job);
        System.err.println("INFO: output schema explicitly set for writing:"
                + s);
        HCatOutputFormat.setSchema(job, s);
        job.setOutputFormatClass(HCatOutputFormat.class);
        return (job.waitForCompletion(true) ? 0 : 1);
    }

    public static void main(String[] args) throws Exception {
        int exitCode = ToolRunner.run(new GroupByAge(), args);
        System.exit(exitCode);
    }
}

Map partitions = new HashMap<String, String>(1);
partitions.put("ds", "20110924");
